use std::{fmt::Display, path::Path, sync::Arc};

pub struct SessionToken(pub String);

impl SessionToken {
    pub fn new() -> Self {
        Self(std::env::var("AOC_SESSION").expect("No AOC_SESSION environment variable set"))
    }
}

impl Display for SessionToken {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        write!(f, "{}", self.0)
    }
}

impl From<String> for SessionToken {
    fn from(value: String) -> Self {
        Self(value)
    }
}

pub struct PuzzleInput(pub String);

impl PuzzleInput {
    pub fn new(day: u8, year: i32) -> Self {
        let input_path = format!("inputs/{}/day_{}.txt", year, day);
        match std::fs::read_to_string(&input_path) {
            Ok(input) => Self(input),
            Err(err) if err.kind() == std::io::ErrorKind::NotFound => {
                Self(Self::fetch_input(day, year).expect("Failed fetching the input online"))
            }
            Err(_) => panic!("Failed to read input file"),
        }
    }

    fn fetch_input(day: u8, year: i32) -> Result<String, Box<dyn std::error::Error>> {
        let url = format!("https://adventofcode.com/{}/day/{}/input", year, day);
        let cookie = reqwest::cookie::Jar::default();
        cookie.add_cookie_str(
            &format!("session={}", SessionToken::new()),
            &url.parse::<reqwest::Url>().unwrap(),
        );
        let client = reqwest::blocking::Client::builder()
            .cookie_provider(Arc::new(cookie))
            .user_agent("Advent of Utils by Itron-al-Lenn found on github.com/Itron-al-Lenn/Advent-of-Utils")
            .build()
            .expect("Failed building the client");

        let response = client.get(url).send()?.text()?;
        let input_path = format!("inputs/{}/day_{}.txt", year, day);

        if let Some(parent) = Path::new(&input_path).parent() {
            std::fs::create_dir_all(parent)?;
        }

        std::fs::write(&input_path, &response)?;

        Ok(response)
    }
}

