use std::{
    fs::{create_dir_all, File},
    io::Write,
    path::Path,
    sync::Arc,
};

pub struct AocHelper {
    year: u16,
    day: u8,
    token: String,
    input_path: String,
}

impl AocHelper {
    pub fn new(year: u16, day: u8) -> AocHelper {
        // TODO: Validate year and day
        let token = std::env::var("AOC_SESSION").expect("No AOC_SESSION environement variable set");
        let input_path = format!("inputs/{}/day_{}.txt", year, day);
        Self {
            day,
            year,
            token,
            input_path,
        }
    }
    fn get_input(&self) -> String {
        match std::fs::read_to_string(self.input_path.clone()) {
            Ok(input) => input,
            Err(err) if err.kind() == std::io::ErrorKind::NotFound => {
                self.fetch_input().expect("Failed to fetch input online")
            }
            Err(_) => panic!("Failed to read input file"),
        }
    }
    fn fetch_input(&self) -> Result<String, Box<dyn std::error::Error>> {
        let url = format!(
            "https://adventofcode.com/{}/day/{}/input",
            self.year, self.day
        );
        let cookie = reqwest::cookie::Jar::default();
        cookie.add_cookie_str(
            &format!("session={}", self.token),
            &url.parse::<reqwest::Url>().unwrap(),
        );
        let client = reqwest::blocking::Client::builder()
            .cookie_provider(Arc::new(cookie))
            .user_agent("Advent of Utils by Itron-al-Lenn found on github.com/Itron-al-Lenn/Advent-of-Utils")
            .build()?;

        let response = client.get(url).send()?;
        let input = String::from_utf8(response.bytes()?.to_vec())?;

        if let Some(parent) = Path::new(&self.input_path).parent() {
            create_dir_all(parent)?;
        }

        let mut output_file = File::create(self.input_path.clone())?;
        let _ = output_file.write_all(input.as_bytes());

        Ok(input)
    }
}

